// load config
var path    = require('path')
var ssbKeys = require('ssb-keys')
var config  = require('ssb-config/inject')(process.env.ssb_appname)
config.keys = ssbKeys.loadOrCreateSync(path.join(config.path, 'secret'))
if(config.keys.curve === 'k256')
  throw new Error('k256 curves are no longer supported,'+
                  'please delete' + path.join(config.path, 'secret'))


// run electron-specific code, if appropriate
if (process.versions['electron']) {
  require('./electron')(config)
} else {
  require('./server-process')(config)
}

var fs = require('fs')
module.exports = function (conf) {
  // ask the oracle thy question
  var oracle = {
    getHostname: function () {
      return conf.host || 'localhost'
    },
    getPort: function () {
      return 7777
    },
    getLocalUrl: function () {
      return (oracle.useTLS()?'https':'http')+'://'+oracle.getHostname()+':'+oracle.getPort()
    },

    allowRemoteAccess: function () {
      return conf.allowRemoteAccess
    },

    requiresPassword: function () {
      return isString(conf.singleUserPassword)
    },
    checkPassword: function (str) {
      return str === conf.singleUserPassword
    },

    useTLS: function () {
      return !!conf.tls
    },
    validateTLS: function () {
      if (!isObject(conf.tls)) {
        console.log('ERROR! tls config requires tls.key (path to the keyfile) and tls.cert (path to the certfile)')
        return false
      }
      if (!isString(conf.tls.key)) {
        console.log('ERROR! tls.key, a path to your TLS key, is required for TLS to work.')
        return false
      }
      if (!isString(conf.tls.cert)) {
        console.log('ERROR! tls.cert, a path to your TLS cert, is required for TLS to work.')
        return false
      }
      try {
        var keyStat = fs.statSync(conf.tls.key)
        if (!keyStat.isFile())
          throw "err"
      } catch (e) {
        console.log('ERROR! the file specified by the tls.key path was not valid.')
        return false
      }
      try {
        var certStat = fs.statSync(conf.tls.cert)
        if (!certStat.isFile())
          throw "err"
      } catch (e) {
        console.log('ERROR! the file specified by the tls.cert path was not valid.')
        return false
      }
      return true
    },
    getTLS: function () {
      return {
        key: fs.readFileSync(conf.tls.key),
        cert: fs.readFileSync(conf.tls.cert)
      }
    },

    // function to validate the config choices made
    hasError: function () {
      // error: allowing remote access but without a TLS cert
      if (this.allowRemoteAccess() && !this.useTLS())
        return true
      // error: bad TLS config
      if (this.useTLS() && !this.validateTLS())
        return true
    },
    allowUnsafe: function () {
      return conf.unsafe
    }
  }

  // give the user some feedback about the config state

  // password config
  if (oracle.requiresPassword())
    console.log('[CFG] Password: YES.')
  else
    console.log('[CFG] Password: NO.')

  // TLS
  if (oracle.useTLS())
    console.log('[CFG] TLS: YES.')
  else
    console.log('[CFG] TLS: NO.')

  // allowRemoteAccess variations
  if (oracle.allowRemoteAccess()) {
    console.log('[CFG] Remote Access: YES.')
    if (!oracle.requiresPassword())
      console.log('WARNING! Remote access is allowed, but no password is configured. This is not safe!')
    if (!oracle.useTLS())
      console.log('ERROR! Remote access is allowed, but no TLS security is configured. Other devices will be able to steal the password or inject attacks.')
  } else
    console.log('[CFG] Remote Access: NO.')

  return oracle
}

function isString (v) {
  return v && typeof v == 'string'
}
function isObject (v) {
  return v && typeof v == 'object'
}

var pull   = require('pull-stream')
var toPull = require('stream-to-pull-stream')
var cat    = require('pull-cat')
var ident  = require('pull-identify-filetype')
var mime   = require('mime-types')
var URL    = require('url')
var path   = require('path')
var fs     = require('fs')
var refs   = require('ssb-ref')
var Stack  = require('stack')
var ip     = require('ip')

var AppCSP = function (req, config) {
  // hostname for the websocket connection:
  // if it's a remote request, always use the configured hostname
  // if it's local, choose from localhost or the configured hostname, based on which the client is using (as revealed by the host header)
  var host
  if (!ip.isLoopback(req.socket.remoteAddress))
    host = config.getHostname()
  else {
    var requestHostname = req.headers.host.split(':')[0] // extract hostname (remove ':port')
    host = (requestHostname == 'localhost' || requestHostname == config.getHostname())
      ? requestHostname
      : (config.getHostname() || 'localhost')

    // final glorious hack: if they're using 127.0.0.1, we should too
    if (requestHostname == '127.0.0.1' && host == 'localhost')
      host = '127.0.0.1'
  }

  return "default-src 'self'; "+
    "connect-src 'self' ws://"+host+":7777 wss://"+host+":7777; "+
    "img-src 'self' data:; "+
    "object-src 'none'; "+
    "frame-src 'none'; "+
    "style-src 'self' 'unsafe-inline'; "+
    "sandbox allow-modals allow-same-origin allow-scripts allow-top-navigation allow-popups"
}
var BlobCSP = function () { return "default-src none; sandbox" }

function respond (res, status, message) {
  res.writeHead(status)
  res.end(message)
}

function respondSource (res, source, wrap) {
  if(wrap) {
    res.writeHead(200, {'Content-Type': 'text/html'})
    pull(
      cat([
        pull.once('<html><body><script>'),
        source,
        pull.once('</script></body></html>')
      ]),
      toPull.sink(res)
    )
  }
  else {
    pull(
      source,
      ident(function (type) {
        if (type) res.writeHead(200, {'Content-Type': mime.lookup(type)})
      }),
      toPull.sink(res)
    )
  }
}

var Log = exports.Log = function (sbot) {
  return function (req, res, next) {
    sbot.emit('log:info', ['HTTP', null, req.method + ' ' + req.url])
    next()
  }
}

var DeviceAccessControl = exports.DeviceAccessControl = function (config) {
  return function (req, res, next) {
    if (config.allowRemoteAccess())
      return next() // remote & local access allowed
    if (ip.isLoopback(req.socket.remoteAddress))
      return next() // local access allowed
    respond(res, 403, 'Remote access forbidden') // remote access disallowed
  }
}

var PasswordAccessControl = exports.DeviceAccessControl = function (config) {
  return function (req, res, next) {
    if (!config.requiresPassword(config))
      return next() // no password required

    // check the password
    var authMatch = /^Basic (.*)$/i.exec(req.headers.authorization)
    if (authMatch) {
      var password = (new Buffer(authMatch[1], 'base64').toString()).split(':')[1]
      if (password && config.checkPassword(password))
        return next() // password checks out
    }

    // deny
    res.setHeader('WWW-Authenticate', 'Basic realm=Authorization Required')
    respond(res, 401, 'Unauthorized')
  }
}

var ServeApp = exports.ServeApp = function (sbot, opts, config) {
  if (!opts || !opts.uiPath)
    throw "opts.uiPath is required"
  return function (req, res, next) {
    var parsed = URL.parse(req.url, true)
    var pathname = parsed.pathname
    if (pathname == '/')
      pathname = '/main.html'

    // dynamic route: manifest.js
    if (pathname == '/manifest.js') {
      res.setHeader('Content-Security-Policy', BlobCSP())
      return respondSource(res, pull.once('window.MANIFEST='+JSON.stringify(sbot.manifest())+';'))
    } 

    // static files
    var filepath = path.join(opts.uiPath, pathname)
    fs.stat(filepath, function (err, stat) {
      if(err) return next()
      if(!stat.isFile()) return respond(res, 403, 'May only load files')

      // set a special CSP for the main HTML file
      if (pathname == '/main.html')
        res.setHeader('Content-Security-Policy', AppCSP(req, config)) // only give the open perms to main.html
      else
        res.setHeader('Content-Security-Policy', BlobCSP())

      respondSource(res, toPull.source(fs.createReadStream(filepath)))
    })
  }
}

var ServeBlobs = exports.ServeBlobs = function (sbot) {
  return function (req, res, next) {
    var parsed = URL.parse(req.url, true)
    var hash = decodeURIComponent(parsed.pathname.slice(1))
    sbot.blobs.want(hash, function(err, has) {
      if (!has) return respond(res, 404, 'File not found')

      // optional name override
      if (parsed.query.name)
        res.setHeader('Content-Disposition', 'inline; filename='+encodeURIComponent(parsed.query.name))

      // serve
      res.setHeader('Content-Security-Policy', BlobCSP())
      respondSource(res, sbot.blobs.get(hash), false)
    })
  }
}

var ServeFiles = exports.ServeFiles = function () {
  return function (req, res, next) {
    var parsed = URL.parse(req.url, true)
    fs.stat(parsed.pathname, function (err, stat) {
      if(err) return respond(res, 404, 'File not found')
      if(!stat.isFile()) return respond(res, 403, 'May only load files')
      res.setHeader('Content-Security-Policy', BlobCSP())
      respondSource(
        res,
        toPull.source(fs.createReadStream(parsed.pathname)),
        false
      )
    })
  }
}

exports.BlobStack = function (sbot, opts) {
  return Stack(
    Log(sbot),
    DeviceAccessControl(),
    ServeBlobs(sbot)
  )
}

exports.FileStack = function (opts) {
  return Stack(
    Log(sbot),
    DeviceAccessControl(),
    ServeFiles()
  )
}

exports.AppStack = function (sbot, opts, config) {
  return Stack(
    Log(sbot),
    PasswordAccessControl(config),
    DeviceAccessControl(config),
    ServeApp(sbot, opts, config),
    ServeBlobs(sbot)
  )
}

var path    = require('path')
var ssbKeys = require('ssb-keys')

module.exports = function (config, cb) {
  // load config

  // load languages
  var t = require('patchwork-translations')
  t.setLocale(require('os-locale').sync())

  // validate the config
  var configOracle = require('./config')(config)
  if (configOracle.hasError()) {
    if (configOracle.allowUnsafe())
      console.log('\n' + t('unsafeConfigIgnore'))
    else {
      console.log('\n' + t('unsafeConfigAbort'))
      return
    }
  }

  logLicense() // per the GPL's recommendation, let ppl know the license

  console.log(t('Starting'))

  // start sbot
  var createSbot = require('scuttlebot')
    .use(require('scuttlebot/plugins/master'))
    .use(require('./lib/persistent-gossip')) // override
    .use(require('./lib/friends-with-gossip-priority'))
    .use(require('scuttlebot/plugins/replicate'))
    .use(require('ssb-blobs'))
    .use(require('scuttlebot/plugins/invite'))
    .use(require('scuttlebot/plugins/block'))
    .use(require('scuttlebot/plugins/logging'))
    .use(require('scuttlebot/plugins/private'))
    .use(require('scuttlebot/plugins/local'))
    .use(require('ssb-notifier'))
    .use(require('./api'))

  var sbot = createSbot(config)

  // write manifest file
  var fs = require('fs')
  fs.writeFileSync(
    path.join(config.path, 'manifest.json'),
    JSON.stringify(sbot.getManifest(), null, 2)
  )

  // setup server
  var http = require('http')
  var https = require('https')
  var ws = require('pull-ws-server')
  var httpStack = require('./http-server')
  var httpServerFn = httpStack.AppStack(sbot, { uiPath: path.join(__dirname, 'ui') }, configOracle)
  var wsServerFn = require('./ws-server')(sbot)

  var serverOpts = configOracle.useTLS() ? configOracle.getTLS() : {}
  serverOpts.verifyClient = require('./ws-server').verifyClient(configOracle)
  var server = ws.createServer(serverOpts)
  server.on('error', fatalError)
  server.on('connection', wsServerFn)
  server.on('request', httpServerFn)
  server.listen(configOracle.getPort())
  if (cb) {
    server.once('listening', cb)
  }
  console.log(t('ServingAt', {url: configOracle.getLocalUrl()}))

  // basic error handling
  function fatalError (e) {
    if (e.code === 'EADDRINUSE')
      console.error('\n' + t('PortInUse', {port: e.port}) + '\n')
    else
      console.error(e.stack || e.toString())
    process.exit(1)
  }
  process.on('uncaughtException', fatalError)

  function logLicense () {
    console.log(t('LicenseConsole', {years: '2015-2016'}) + '\n')
  }
}

var muxrpc = require('muxrpc')
var pull = require('pull-stream')
var Serializer = require('pull-serializer')

module.exports = function (sbot, opts) {
  return function (stream) {
    // create rpc object
    var rpc = muxrpc({}, sbot.manifest(), serialize)(sbot)
    rpc.authorized = { id: sbot.id, role: 'master' }

    // start the stream
    pull(stream, rpc.createStream(), stream)
  }
}

function serialize (stream) {
  return Serializer(stream, JSON, {split: '\n\n'})
}

module.exports.verifyClient = function (config) {
  return function (info) {
    return info.origin === config.getLocalUrl()
  }
}

var fs          = require('fs')
var pull        = require('pull-stream')
var multicb     = require('multicb')
var pl          = require('pull-level')
var pushable    = require('pull-pushable')
var paramap     = require('pull-paramap')
var cat         = require('pull-cat')
var Notify      = require('pull-notify')
var toPull      = require('stream-to-pull-stream')
var ref         = require('ssb-ref')
var pathlib     = require('path')
var mlib        = require('ssb-msgs')
var threadlib   = require('patchwork-threads')
var u           = require('./util')

exports.name        = 'patchwork'
exports.version     = '1.0.0'
exports.manifest    = require('./manifest')
exports.permissions = require('./permissions')

exports.init = function (sbot, opts) {

  var api = {}
  var patchworkdb = sbot.sublevel('patchwork')
  var db = {
    isread: patchworkdb.sublevel('isread'),
    bookmarked: patchworkdb.sublevel('bookmarked'),
    channelpinned: patchworkdb.sublevel('channelpinned'),
    channelwatched: patchworkdb.sublevel('channelwatched')
  }
  var state = {
    // indexes (lists of {key:, ts:})
    mymsgs: [],
    inbox: u.index('inbox'),
    bookmarks: u.index('bookmarks'),
    mentions: u.index('mentions'),
    notices: u.index('notices'),
    privatePosts: u.index('privatePosts'),
    publicPosts: u.index('publicPosts'),
    // other indexes: channel-* are created as needed

    // views
    profiles: {},
    names: {}, // ids -> names
    ids: {}, // names -> ids
    actionItems: {}
  }

  // track sync state
  // - processor does async processing for each message that comes in
  // - awaitSync() waits for that processing to finish
  // - pinc() on message arrival, pdec() on message processed
  // - nP === 0 => all messages processed
  var nP = 0, syncCbs = []
  function awaitSync (cb) {
    if (nP > 0)
      syncCbs.push(cb)
    else cb()
  }
  state.pinc = function () { nP++ }
  state.pdec = function () {
    nP--
    if (nP === 0) {
      syncCbs.forEach(function (cb) { cb() })
      syncCbs.length = 0
    }
  }

  // load bookmarks into indexes
  pull(
    pl.read(db.bookmarked, { keys: true, values: false }),
    pull.asyncMap(function (key, cb) {
      // fetch the message and isread data
      var obj = { key: key, value: null, isread: false }
      db.isread.get(key, function (err, isread) { obj.isread = isread; done() })
      sbot.get(key, function (err, value) { obj.value = value; done() })
      var n=0;
      function done() {
        if (++n == 2) cb(null, obj)
      }
    }),
    pull.drain(
      function (msg) {
        if (msg.value) {
          // add to the inbox index
          row = state.inbox.sortedUpsert(msg.value.timestamp, msg.key)
          row.isread = msg.isread

          // add to the bookmarks index, only if the wont already be in the private msgs index
          if (msg.value && typeof msg.value.content != 'string') {
            var row = state.bookmarks.sortedUpsert(msg.value.timestamp, msg.key)
            row.isread = msg.isread
          }
        }
      }
    )
  )

  // load channelpins into indexes
  pull(
    pl.read(db.channelpinned, { keys: true, values: true }),
    pull.drain(function (pin) {
      if (typeof pin.key === 'string') {
        var index = getChannelIndex(pin.key)
        index.pinned = pin.value
      }
    })
  )

  // load channelwatches into indexes
  state.pinc()
  pull(
    pl.read(db.channelwatched, { keys: true, values: true }),
    pull.drain(function (watch) {
      if (typeof watch.key === 'string') {
        var index = getChannelIndex(watch.key)
        index.watched = watch.value
      }
    },
    function () { state.pdec() })
  )

  // setup sbot log processor
  var isHistorySynced = false // track so we dont emit events for old messages
  var processor = require('./processor')(sbot, db, state, emit)
  awaitSync(function () { // wait for channelwatches to load up
    // create log stream
    pull(pl.read(sbot.sublevel('log'), { live: true, onSync: onHistorySync }), pull.drain(processor))
    state.pinc()
    function onHistorySync () {
      console.log('Log history read...')
      // when all current items finish, consider prehistory synced (and start emitting)
      awaitSync(function () { 
        console.log('Indexes generated')
        isHistorySynced = true
      })
      state.pdec()
    }
  })

  // events stream
  var notify = Notify()
  function emit (type, data) {
    if (!isHistorySynced)
      return
    var e = data || {}
    e.type = type
    if (e.type == 'index-change') {
      api.getIndexCounts(function (err, counts) {
        e.counts = counts
        e.total = counts[e.index]
        e.unread = counts[e.index+'Unread']
        notify(e)
      })
    } else
      notify(e)
  }

  // getters

  api.createEventStream = function () {
    return notify.listen()
  }

  api.getMyProfile = function (cb) {
    awaitSync(function () {
      api.getProfile(sbot.id, cb)
    })
  }

  api.getIndexCounts = function (cb) {
    awaitSync(function () {
      var counts = {
        inboxUnread: state.inbox.filter(function (row) { return !row.isread }).length,
        bookmarkUnread: state.bookmarks.filter(function (row) { return !row.isread }).length,
        mentionUnread: state.mentions.filter(function (row) { return !row.isread }).length,
        privateUnread: state.privatePosts.filter(function (row) { return !row.isread }).length,
        noticesUnread: state.notices.countUntouched()
      }
      cb(null, counts)
    })
  }

  api.createInboxStream = indexStreamFn(state.inbox)
  api.createBookmarkStream = indexStreamFn(state.bookmarks)
  api.createMentionStream = indexStreamFn(state.mentions)
  api.createNoticeStream = indexStreamFn(state.notices)
  api.createPrivatePostStream = indexStreamFn(state.privatePosts)
  api.createPublicPostStream = indexStreamFn(state.publicPosts)
  api.createChannelStream = function (channel, opts) {
    if (typeof channel !== 'string' || !channel.trim())
      return cb(new Error('Invalid channel'))
    var index = getChannelIndex(channel)
    return indexStreamFn(index)(opts)
  }
  api.createSearchStream = function (opts) {
    opts = opts || {}
    var searchRegex = new RegExp(opts.query||'', 'i')

    var limit = opts.limit
    delete opts.limit

    opts.type = 'post'
    return pull(
      sbot.messagesByType(opts),
      pull.asyncMap(function (msg, cb) {
        // decrypt the message, if needed
        threadlib.decryptThread(sbot, msg, cb)
      }),
      pull.filter(function (msg) {
        // run search filter
        return searchRegex.test(''+msg.value.content.text)
      }),
      limit ? pull.take(limit) : undefined
    )
  }

  function indexMarkRead (indexname, key, keyname) {
    if (Array.isArray(key)) {
      key.forEach(function (k) {
        indexMarkRead(indexname, k, keyname)
      })
      return
    }

    var index = state[indexname]
    var row = index.find(key, keyname)
    if (row) {
      var wasread = row.isread
      row.isread = true
      if (!wasread)
        emit('index-change', { index: indexname })
      return true
    }
  }

  function indexMarkUnread (indexname, key, keyname) {
    if (Array.isArray(key)) {
      key.forEach(function (k) {
        indexMarkUnread(indexname, k, keyname)
      })
      return
    }

    var index = state[indexname]
    var row = index.find(key, keyname)
    if (row) {
      var wasread = row.isread
      row.isread = false
      if (wasread)
        emit('index-change', { index: indexname })
      return true
    }
  }

  api.markRead = function (key, cb) {
    awaitSync(function () {
      indexMarkRead('inbox', key)
      indexMarkRead('bookmarks', key)
      indexMarkRead('mentions', key)
      indexMarkRead('privatePosts', key)
      if (Array.isArray(key)) {
        db.isread.batch(key.map(function (k) { return { type: 'put', key: k, value: 1 }}), cb)
        key.forEach(function (key) { emit('isread', { key: key, value: true }) })
      } else {
        db.isread.put(key, 1, cb)
        emit('isread', { key: key, value: true })
      }
    })
  }
  api.markUnread = function (key, cb) {
    awaitSync(function () {
      indexMarkUnread('inbox', key)
      indexMarkUnread('bookmarks', key)
      indexMarkUnread('mentions', key)
      indexMarkUnread('privatePosts', key)
      if (Array.isArray(key)) {
        db.isread.batch(key.map(function (k) { return { type: 'del', key: k }}), cb)
        key.forEach(function (key) { emit('isread', { key: key, value: false }) })
      } else {
        db.isread.del(key, cb) 
        emit('isread', { key: key, value: false })
      }
    })
  }
  api.markAllRead = function (indexName, cb) {
    awaitSync(function () {
      var index = state[indexName]
      if (!index || index.name !== indexName)
        return cb(new Error('Invalid index'))

      var done = multicb()
      index
        .filter(function (row) { return !row.isread })
        .forEach(function (row) { 
          var cb = done()
          threadlib.getPostThread(sbot, row.key, { isRead: true }, function (err, thread) {
            if (err)
              return cb()
            threadlib.markThreadRead(sbot, thread, cb)
          })
        })
      done(cb)
    })
  }
  api.toggleRead = function (key, cb) {
    api.isRead(key, function (err, v) {
      if (!v) {
        api.markRead(key, function (err) {
          cb(err, true)
        })
      } else {
        api.markUnread(key, function (err) {
          cb(err, false)
        })
      }
    })
  }
  api.isRead = function (key, cb) {
    if (Array.isArray(key)) {
      var done = multicb({ pluck: 1 })
      key.forEach(function (k, i) {
        var cb = done()
        db.isread.get(k, function (err, v) { cb(null, !!v) })
      })
      done(cb)
    } else {
      db.isread.get(key, function (err, v) {
        cb && cb(null, !!v)
      })
    }
  }

  api.bookmark = function (key, cb) {
    sbot.get(key, function (err, value) {
      if (err) return cb(err)
      var done = multicb({ pluck: 1, spread: true })
      db.bookmarked.put(key, 1, done()) // update bookmarks index
      u.getThreadHasUnread(sbot, key, done()) // get the target thread's read/unread state
      done(function (err, putRes, hasUnread) {
        // insert into the bookmarks and inbox indexes
        var bookmarksRow = state.bookmarks.sortedUpsert(value.timestamp, key)
        bookmarksRow.isread = !hasUnread // set isread state
        emit('index-change', { index: 'bookmarks' })
        var inboxRow = state.inbox.sortedUpsert(value.timestamp, key)
        inboxRow.isread = !hasUnread // set isread state
        emit('index-change', { index: 'inbox' })
        cb(err, putRes)
      })
    })
  }
  api.unbookmark = function (key, cb) {
    sbot.get(key, function (err, value) {
      if (err) return cb(err)
      state.bookmarks.remove(key)
      state.inbox.remove(key)
      db.bookmarked.del(key, cb) 
    })
  }
  api.toggleBookmark = function (key, cb) {
    api.isBookmarked(key, function (err, v) {
      if (!v) {
        api.bookmark(key, function (err) {
          cb(err, true)
        })
      } else {
        api.unbookmark(key, function (err) {
          cb(err, false)
        })
      }
    })
  }
  api.isBookmarked = function (key, cb) {
    db.bookmarked.get(key, function (err, v) {
      cb && cb(null, !!v)
    })
  }

  function getChannelIndex (channel) {
    var k = 'channel-'+channel
    var index = state[k]
    if (!index)
      index = state[k] = u.index(k)
    return index
  }
  api.getChannels = function (cb) {
    awaitSync(function () {
      var channels = []
      for (var k in state) {
        if (k.indexOf('channel-') === 0) {
          var lastUpdated = (state[k].rows[0]) ? state[k].rows[0].ts : 0
          channels.push({
            name: k.slice('channel-'.length),
            lastUpdated: lastUpdated,
            pinned: state[k].pinned,
            watched: state[k].watched
          })
        }
      }
      cb(null, channels)
    })
  }
  api.pinChannel = function (channel, cb) {
    var index = getChannelIndex(channel)
    index.pinned = true
    db.channelpinned.put(channel, 1, cb)
    emit('channelpinned', { channel: channel, value: true })
  }
  api.unpinChannel = function (channel, cb) {
    var index = getChannelIndex(channel)
    index.pinned = false
    db.channelpinned.del(channel, cb) 
    emit('channelpinned', { channel: channel, value: false })
  }
  api.toggleChannelPinned = function (channel, cb) {
    var index = getChannelIndex(channel)
    if (index.pinned) {
      api.unpinChannel(channel, function (err) {
        cb(err, true)
      })
    } else {
      api.pinChannel(channel, function (err) {
        cb(err, false)
      })
    }
  }
  api.watchChannel = function (channel, cb) {
    var index = getChannelIndex(channel)
    index.watched = true
    db.channelwatched.put(channel, 1, cb)
    emit('channelwatched', { channel: channel, value: true })
  }
  api.unwatchChannel = function (channel, cb) {
    var index = getChannelIndex(channel)
    index.watched = false
    db.channelwatched.del(channel, cb) 
    emit('channelwatched', { channel: channel, value: false })
  }
  api.toggleChannelWatched = function (channel, cb) {
    var index = getChannelIndex(channel)
    if (index.watched) {
      api.unwatchChannel(channel, function (err) {
        cb(err, true)
      })
    } else {
      api.watchChannel(channel, function (err) {
        cb(err, false)
      })
    }
  }

  api.addFileToBlobs = function (base64Buff, cb) {
    return pull(
      pull.values([new Buffer(base64Buff, 'base64')]),
      sbot.blobs.add(cb)
    )
  }
  api.saveBlobToFile = function (hash, path, cb) {
    pull(
      sbot.blobs.get(hash),
      toPull.sink(fs.createWriteStream(path), cb)
    )
  }

  var lookupcodeRegex = /(@[a-z0-9\/\+\=]+\.[a-z0-9]+)(?:\[via\])?(.+)?/i
  api.useLookupCode = function (code) {
    var eventPush = pushable()

    // parse and validate the code
    var id, addrs
    var parts = lookupcodeRegex.exec(code)
    var valid = true
    if (parts) {
      id  = parts[1]
      addrs = (parts[2]) ? parts[2].split(',') : []

      // validate id
      if (!ref.isFeedId(id))
        valid = false

      // parse addresses
      addrs = addrs
        .map(function (addr) {
          addr = addr.split(':')
          if (addr.length === 3)
            return { host: addr[0], port: +addr[1], key: addr[2] }
        })
        .filter(Boolean)
    } else
      valid = false

    if (!valid) {
      eventPush.push({ type: 'error', message: 'Invalid lookup code' })
      eventPush.end()
      return eventPush
    }

    // begin the search!
    search(addrs.concat(sbot.gossip.peers()))
    function search (peers) {
      var peer = peers.pop()
      if (!peer)
        return eventPush.end()

      // connect to the peer
      eventPush.push({ type: 'connecting', addr: peer })      
      sbot.connect(peer, function (err, rpc) {
        if (err) {
          eventPush.push({ type: 'error', message: 'Failed to connect', err: err })
          return search(peers)
        }
        // try a sync
        sync(rpc, function (err, seq) { 
          if (seq > 0) {
            // success!
            eventPush.push({ type: 'finished', seq: seq })
            eventPush.end()
          } else
            search(peers) // try next
        })
      })
    }

    function sync (rpc, cb) {
      // fetch the feed
      var seq
      eventPush.push({ type: 'syncing', id: id })
      pull(
        rpc.createHistoryStream({ id: id, keys: false }),
        pull.through(function (msg) {
          seq = msg.sequence
        }),
        sbot.createWriteStream(function (err) {
          cb(err, seq)
        })
      )
    }

    return eventPush
  }

  api.getProfile = function (id, cb) {
    awaitSync(function () { cb(null, state.profiles[id]) })
  }
  api.getAllProfiles = function (cb) {
    awaitSync(function () { cb(null, state.profiles) })
  }
  api.getNamesById = function (cb) {
    awaitSync(function () { cb(null, state.names) })
  }
  api.getIdsByName = function (cb) {
    awaitSync(function () { cb(null, state.ids) })
  }
  api.getName = function (id, cb) {
    awaitSync(function () { cb(null, state.names[id]) })
  }
  api.getActionItems = function (cb) {
    awaitSync(function () { cb(null, state.actionItems) })
  }

  // helper to get an option off an opt function (avoids the `opt || {}` pattern)
  function o (opts, k, def) {
    return opts && opts[k] !== void 0 ? opts[k] : def
  }

  // helper to get messages from an index
  function indexStreamFn (index, getkey) {
    return function (opts) {
      var lastAccessed = index.lastAccessed
      index.touch()

      // emulate the `ssb.createFeedStream` interface
      var lt      = o(opts, 'lt')
      var lte     = o(opts, 'lte')
      var gt      = o(opts, 'gt')
      var gte     = o(opts, 'gte')
      var limit   = o(opts, 'limit')
      var threads = o(opts, 'threads')
      var unread  = o(opts, 'unread')

      // lt, lte, gt, gte should look like:
      // [msg.value.timestamp, msg.value.author]

      // helper to create emittable rows
      function lookup (row) {
        if (!row) return
        var key = (getkey) ? getkey(row) : row.key
        if (key) {
          var rowcopy = { key: key }
          for (var k in row) { // copy index attrs into rowcopy
            if (!rowcopy[k]) rowcopy[k] = row[k]
          }
          return rowcopy
        }
      }

      // helper to fetch rows
      function fetch (row, cb) {
        if (threads) {
          threadlib.getPostSummary(sbot, row.key, { isBookmarked: true, isRead: true, votes: true, mentions: sbot.id }, function (err, thread) {
            for (var k in thread)
              row[k] = thread[k]
            cb(null, row)
          })
        } else {
          sbot.get(row.key, function (err, value) {
            // if (err) {
              // suppress this error
              // the message isnt in the local cache (yet)
              // but it got into the index, likely due to a link
              // instead of an error, we'll put a null there to indicate the gap
            // }
            row.value = value
            cb(null, row)
          })
        }
      }

      // readstream
      var readPush = pushable()
      var read = pull(readPush, paramap(fetch))

      // await sync, then emit the reads
      awaitSync(function () {
        var added = 0
        for (var i=0; i < index.rows.length; i++) {
          var row = index.rows[i]

          if (limit && added >= limit)
            break

          // we're going to only look at timestamp, because that's all that the index tracks
          var invalid = !!(
            (lt  && row.ts >= lt[0]) ||
            (lte && row.ts > lte[0]) ||
            (gt  && row.ts <= gt[0]) ||
            (gte && row.ts < gte[0]) ||
            (unread && row.isread)
          )
          if (invalid)
            continue

          var r = lookup(row)
          if (r) {
            r.isNew = r.ts > lastAccessed
            readPush.push(r)
            added++
          }
        }
        readPush.end()
      })

      if (opts && opts.live) {
        // live stream, concat the live-emitter on the end
        index.on('add', onadd)
        var livePush = pushable(function () { index.removeListener('add', onadd) })
        function onadd (row) { livePush.push(lookup(row)) }
        var live = pull(livePush, paramap(fetch))
        return cat([read, live])
      }
      return read
    }
  }

  return api
}

var mlib = require('ssb-msgs')
var multicb = require('multicb')
var EventEmitter = require('events').EventEmitter
var threadlib = require('patchwork-threads')

// constant used to decide if an index-entry was recent enough to emit an 'add' event for
var IS_RECENT_MAX = 1e3 * 60 * 60 * 48 // 2 days

module.exports.index = function (name) {
  var index = new EventEmitter()
  index.name = name
  index.rows = []
  index.lastAccessed = Date.now()

  index.touch = function () {
    index.lastAccessed = Date.now()
  }

  index.sortedInsert = function (ts, key) {
    var row = (typeof ts == 'object') ? ts : { ts: ts, key: key }
    for (var i=0; i < index.rows.length; i++) {
      if (index.rows[i].ts < row.ts) {
        index.rows.splice(i, 0, row)
        if (timestampIsRecent(row.ts))
          index.emit('add', row)
        return row
      }
    }
    index.rows.push(row)
    if (timestampIsRecent(row.ts))
      index.emit('add', row)
    return row
  }

  index.sortedUpdate = function (ts, key) {
    var i = index.indexOf(key)
    if (i !== -1) {
      // readd to index at new TS
      if (index.rows[i].ts < ts) {
        var row = index.rows[i]
        // remove from old position
        index.rows.splice(i, 1)
        // update values
        row.ts = ts
        // reinsert
        index.sortedInsert(row)
        return row
      } else
        return index.rows[i]
    }
  }

  index.sortedUpsert = function (ts, key) {
    var row = index.sortedUpdate(ts, key)
    if (!row) {
      // add to index
      row = index.sortedInsert(ts, key)
    }
    return row
  }

  index.remove = function (key, keyname) {
    var i = index.indexOf(key, keyname)
    if (i !== -1)
      index.rows.splice(i, 1)
  }

  index.indexOf = function (key, keyname) {
    keyname = keyname || 'key'
    for (var i=0; i < index.rows.length; i++) {
      if (index.rows[i][keyname] === key)
        return i
    }
    return -1
  }

  index.find = function (key, keyname) {
    var i = index.indexOf(key, keyname)
    if (i !== -1)
      return index.rows[i]
    return null
  }

  index.contains = function (key) {
    return index.indexOf(index, key) !== -1
  }

  index.filter = index.rows.filter.bind(index.rows)

  // helper to count # of messages that are new
  index.countUntouched = function () {
    // iterate until we find a ts older than lastAccessed, then return that #
    for (var i=0; i < index.rows.length; i++) {
      if (index.rows[i].ts < index.lastAccessed)
        return i
    }
    return 0
  }

  function timestampIsRecent (ts) {
    var now = Date.now()
    var delta = Math.abs(now - ts)
    return (delta < IS_RECENT_MAX)
  }

  return index
}

module.exports.getThreadHasUnread = function (sbot, msg, cb) {
  threadlib.getParentPostSummary(sbot, msg, { isRead: true }, function (err, thread) {
    if (err) return cb(err)
    cb(err, thread.hasUnread)
  })
}

module.exports.findLink = function (links, id) {
  for (var i=0; i < (links ? links.length : 0); i++) {
    if (links[i].link === id)
      return links[i]
  }
}

var mlib = require('ssb-msgs')
var u = require('./util')

module.exports = function (sbot, db, state, emit) {

  var processors = {
    post: function (msg) {
      var me = getProfile(sbot.id)
      var author = msg.value.author
      var by_me = (author === sbot.id)
      var c = msg.value.content
      var root = mlib.link(c.root, 'msg')
      var recps = mlib.links(c.recps)
      var mentions = mlib.links(c.mentions)

      // inbox index:
      // add msgs that mention or address the user
      var inboxRow
      if (u.findLink(mentions, sbot.id) || u.findLink(recps, sbot.id)) {
        // if a reply, make sure we have the root already
        if (root)
          inboxRow = state.inbox.sortedUpdate(msg.received, root.link)
        if (!inboxRow) {
          // fallback to inserting the current message, if the root isnt available (or its not a reply)
          inboxRow = state.inbox.sortedUpsert(msg.received, msg.key)
        }
        emit('index-change', { index: 'inbox' })
        attachChildIsRead(inboxRow, msg.key)
      }
      // update for replies
      else if (root) {
        inboxRow = state.inbox.find(root.link)
        if (inboxRow) {
          state.inbox.sortedUpsert(msg.received, root.link)
          emit('index-change', { index: 'inbox' })
          attachChildIsRead(inboxRow, msg.key)
        }
      }

      // inbox sub-indexes
      // note, these sub-indexes shouldnt have overlap: thus the else/if
      // private posts index
      var bookmarkRow
      if (u.findLink(recps, sbot.id)) {
        var privatePostsRow
        // update replies
        if (root) {
          privatePostsRow = state.privatePosts.find(root.link)
          if (privatePostsRow) {
            state.privatePosts.sortedUpsert(ts(msg), root.link)
            emit('index-change', { index: 'privatePosts' })
            attachChildIsRead(privatePostsRow, msg.key)
          }
        }
        // add msgs addressed to the user
        else if (!privatePostsRow) {        
          privatePostsRow = state.privatePosts.sortedUpsert(ts(msg), root ? root.link : msg.key)
          emit('index-change', { index: 'privatePosts' })
          attachChildIsRead(privatePostsRow, msg.key)
        }
      }
      // bookmarks index: update for replies
      else if (root && (bookmarkRow = state.bookmarks.find(root.link))) {
        state.bookmarks.sortedUpsert(ts(msg), root.link)
        emit('index-change', { index: 'bookmarks' })
        attachChildIsRead(bookmarkRow, msg.key)
      }
      // mentions index: add msgs that mention the user
      else if (u.findLink(mentions, sbot.id)) {
        var mentionRow = state.mentions.sortedUpsert(ts(msg), root ? root.link : msg.key)
        emit('index-change', { index: 'mentions' })
        attachChildIsRead(mentionRow, msg.key)
      }

      // public posts index: add public posts / replies
      if (recps.length === 0) {
        state.publicPosts.sortedUpsert(ts(msg), root ? root.link : msg.key)
        emit('index-change', { index: 'publicPosts' })
      }

      if (c.channel && typeof c.channel === 'string') {
        // channels index: add public posts / replies
        var indexName = 'channel-'+c.channel
        var index = state[indexName] = (state[indexName] || u.index(indexName))
        index.sortedUpsert(msg.value.timestamp, root ? root.link : msg.key)
        emit('index-change', { index: indexName })

        // inbox index: add watched channels
        if (index.watched) {
          var inboxRow
          if (root)
            // only do updates if a reply, we dont want inbox items that dont have a root
            inboxRow = state.inbox.sortedUpdate(ts(msg), root.link)
          else
            inboxRow = state.inbox.sortedInsert(ts(msg), msg.key)
          if (inboxRow) {
            emit('index-change', { index: 'inbox' })
            attachChildIsRead(inboxRow, msg.key)
          }
        }
      }
    },

    contact: function (msg) {      
      mlib.links(msg.value.content.contact, 'feed').forEach(function (link) {
        // update profiles
        var toself = link.link === msg.value.author
        if (toself) updateSelfContact(msg.value.author, msg)
        else        updateOtherContact(msg.value.author, link.link, msg)

        // notices index: add follows or blocks
        if (link.link === sbot.id && ('following' in msg.value.content || 'blocking' in msg.value.content)) {
          state.notices.sortedUpsert(ts(msg), msg.key)
          emit('index-change', { index: 'notices' })
        }
      })
    },

    about: function (msg) {
      // update profiles
      mlib.links(msg.value.content.about, 'feed').forEach(function (link) {
        var toself = link.link === msg.value.author
        if (toself) updateSelfContact(msg.value.author, msg)
        else        updateOtherContact(msg.value.author, link.link, msg)
      })
    },

    vote: function (msg) {
      // notices index: add upvotes on your messages
      var msgLink = mlib.link(msg.value.content.vote, 'msg')
      if (msgLink && state.mymsgs.indexOf(msgLink.link) >= 0 && msgLink.value > 0) {
        state.notices.sortedUpsert(ts(msg), msg.key)
        emit('index-change', { index: 'notices' })
      }

      // user flags
      var voteLink = mlib.link(msg.value.content.vote, 'feed')
      if (voteLink) {
        var target = getProfile(voteLink.link)
        if (voteLink.value < 0)
          target.flaggers[msg.value.author] = { msgKey: msg.key, reason: voteLink.reason }
        else
          delete target.flaggers[msg.value.author]
      }
    }
  }

  function getProfile (pid) {
    if (pid.id) // already a profile?
      return pid

    var profile = state.profiles[pid]
    if (!profile) {
      state.profiles[pid] = profile = {
        id: pid,
        self: { name: null, image: null }, // values set by this user about this user
        byMe: { name: null, image: null }, // values set by the local user about this user
        names: {}, // map of name -> array of users to use that name
        images: {}, // map of images -> array of users to use that pic
        followers: {}, // map of followers -> true
        flaggers: {}, // map of flaggers -> flag-msg
      }
    }
    return profile
  }

  function updateSelfContact (author, msg) {
    var c = msg.value.content
    author = getProfile(author)

    // name: a non-empty string
    if (nonEmptyStr(c.name)) {
      var safeName = makeNameSafe(c.name)

      // remove old assignment, if it exists
      for (var name in author.names) {
        author.names[name] = author.names[name].filter(function (id) { return id !== author.id })
        if (!author.names[name][0])
          delete author.names[name]
      }

      // add new assignment
      author.self.name = safeName
      author.names[safeName] = (author.names[safeName]||[]).concat(author.id)
      rebuildNamesFor(author)
    }

    // image: link to image
    if ('image' in c) {
      var imageLink = mlib.link(c.image, 'blob')
      if (imageLink) {
        // remove old assignment, if it exists
        for (var image in author.images) {
          author.images[image] = author.images[image].filter(function (id) { return id !== author.id })
          if (!author.images[image][0])
            delete author.images[image]
        }

        // add new assignment
        author.self.image = imageLink
        if (author.id == sbot.id)
          author.byMe.image = imageLink
        author.images[imageLink.link] = (author.images[imageLink.link]||[]).concat(author.id)
      }
    }
  }

  function updateOtherContact (source, target, msg) {
    var c = msg.value.content
    source = getProfile(source)
    target = getProfile(target)

    // name: a non-empty string
    if (nonEmptyStr(c.name)) {
      var safeName = makeNameSafe(c.name)

      // remove old assignment, if it exists
      for (var name in target.names) {
        target.names[name] = target.names[name].filter(function (id) { return id !== source.id })
        if (!target.names[name][0])
          delete target.names[name]
      }

      // add new assignment
      target.names[safeName] = (target.names[safeName]||[]).concat(source.id)
      if (source.id === sbot.id)
        target.byMe.name = safeName
      rebuildNamesFor(target)
    }

    // image: link to image
    if ('image' in c) {
      var imageLink = mlib.link(c.image, 'blob')
      if (imageLink) {
        // remove old assignment, if it exists
        for (var image in target.images) {
          target.images[image] = target.images[image].filter(function (id) { return id !== source.id })
          if (!target.images[image][0])
            delete target.images[image]
        }

        // add new assignment
        target.images[imageLink.link] = (target.images[imageLink.link]||[]).concat(source.id)
        if (source.id == sbot.id)
          target.byMe.image = imageLink
      }
    }

    // following: bool
    if (typeof c.following === 'boolean') {
      if (c.following)
        target.followers[source.id] = true
      else
        delete target.followers[source.id]

      // if from the user, update names (in case un/following changes conflict status)
      if (msg.value.author == sbot.id)
        rebuildNamesFor(target)
    }
  }

  function rebuildNamesFor (profile) {
    profile = getProfile(profile)

    // remove oldname from id->name map
    var oldname = state.names[profile.id]
    if (oldname) {
      if (state.ids[oldname] == profile.id) {
        // remove
        delete state.ids[oldname]
      } else if (Array.isArray(state.ids[oldname])) {
        // is in a conflict, remove from conflict array
        var i = state.ids[oldname].indexOf(profile.id)
        if (i !== -1) {
          state.ids[oldname].splice(i, 1)
          if (state.ids[oldname].length === 1) {
            // conflict resolved
            delete state.actionItems[oldname]
            state.ids[oldname] = state.ids[oldname][0]
          }
        }
      }
    }

    // default to self-assigned name
    var name = profile.self.name

    // override with name assigned by the local user
    if (profile.id !== sbot.id && profile.byMe.name)
      name = profile.byMe.name

    // abort if there's no name at all
    if (!name)
      return

    // store
    state.names[profile.id] = name

    // if following, update id->name map
    if (profile.id === sbot.id || profile.followers[sbot.id]) {
      if (!state.ids[name]) { // no conflict?
        // take it
        state.ids[name] = profile.id
      } else {
        // keep track of all assigned ids
        if (Array.isArray(state.ids[name]))
          state.ids[name].push(profile.id)
        else
          state.ids[name] = [state.ids[name], profile.id]
        // conflict, this needs to be handled by the user
        state.actionItems[name] = {
          type: 'name-conflict',
          name: name,
          ids: state.ids[name]
        }
      }
    }
  }

  function attachIsRead (indexRow, key) {
    key = key || indexRow.key
    state.pinc()
    db.isread.get(key, function (err, v) {
      indexRow.isread = !!v
      state.pdec()
    })
  }

  // look up the child and root isread state, combine them with the current row's isread state
  function attachChildIsRead (indexRow, childKey, cb) {
    state.pinc()
    var rootIsRead, childIsRead

    // get child isread
    db.isread.get(childKey, function (err, v) {
      childIsRead = !!v
      next()
    })

    // lookup the root isread from DB if not already on the row
    if (typeof indexRow.isread == 'boolean') {
      rootIsRead = indexRow.isread
    } else {
      db.isread.get(indexRow.key, function (err, v) {
        rootIsRead = !!v
        next()
      })      
    }

    function next () {
      // wait for both
      if (typeof rootIsRead != 'boolean' || typeof childIsRead != 'boolean')
        return

      // combine child and root isread state
      indexRow.isread = rootIsRead && childIsRead

      cb && cb(null, indexRow.isread)
      state.pdec() // call this last, after all async work is done
    }
  }

  function follows (a, b) {
    if (a.id) a = a.id // if `a` is a profile, just get its id
    if (b.id) b = b.id // if `b` is a profile, just get its id
    return (a != b && getProfile(b).followers[a])
  }

  // helper to get the most reliable timestamp for a message
  // - stops somebody from boosting their ranking (accidentally, maliciously) with a future TS
  // - applies only when ordering by most-recent
  function ts (msg) {
    return Math.min(msg.received, msg.value.timestamp)
  }

  // exported api

  function fn (logkey) {
    state.pinc()
    var key = logkey.value
    sbot.get(logkey.value, function (err, value) {
      var msg = { key: key, value: value, received: logkey.key }
      try {
        // encrypted? try to decrypt
        if (typeof value.content == 'string' && value.content.slice(-4) == '.box') {
          value.content = sbot.private.unbox(value.content)
          if (!value.content)
            return state.pdec()
        }

        // collect keys of user's messages
        if (msg.value.author === sbot.id)
          state.mymsgs.push(msg.key)

        // type processing
        var process = processors[msg.value.content.type]
        if (process)
          process(msg)
      }
      catch (e) {
        // :TODO: use sbot logging plugin
        console.error('Failed to process message', e, e.stack, key, value)
      }
      state.pdec()
    })
  }

  return fn
}

function nonEmptyStr (str) {
    return (typeof str === 'string' && !!(''+str).trim())
  }

// allow A-z0-9._-, dont allow a trailing .
var badNameCharsRegex = /[^A-z0-9\._-]/g
function makeNameSafe (str) {
  str = str.replace(badNameCharsRegex, '_')
  if (str.charAt(str.length - 1) == '.')
    str = str.slice(0, -1) + '_'
  return str
}

