}

function setZoom(z) {
  zoom = z
  webFrame.setZoomLevel(zoom)
  localStorage.zoom = z
}

module.exports = {
  setZoom: setZoom,
  zoomIn: function () {
    setZoom(zoom + zoomStep)
  },
  zoomOut: function () {
    setZoom(zoom - zoomStep)
  },
  zoomReset: function () {
    setZoom(0)
  }
}

