Common state which either exists as part of the session,
or which has  been  loaded  from  scuttlebot during page
refresh because  its  commonly  needed during rendering.
*/

// constants
var POLL_PEER_INTERVAL = 5e3 // every 5 seconds

var multicb   = require('multicb')
var pull      = require('pull-stream')
var SSBClient = require('./ws-client')
var emojis    = require('emoji-named-characters')
var Emitter   = require('events')
var extend    = require('xtend/mutable')
var pu        = require('patchkit-util')
var favicon   = require('./favicon')
var createHashHistory = require('history').createHashHistory
var t         = require('patchwork-translations')

// event streams and listeners
var patchworkEventStream = null
var isWatchingNetwork = false

// master state object
var app =
module.exports = extend(new Emitter(), {
  // sbot rpc connection
  ssb: SSBClient(),

  // pull state from sbot, called on every view change
  fetchLatestState: fetchLatestState,

  // ui data
  history: createHashHistory({ queryKey: false }),
  isComposerOpen: false,
  suggestOptions: { 
    ':': Object.keys(emojis).map(function (emoji) {
      return {
        image: './img/emoji/' + emoji + '.png',
        title: emoji,
        subtitle: emoji,
        value: ':' + emoji + ':'
      }
    }),
    '@': []
  },
  issues: [],
  issue: addIssue.bind(null, true), // helper to add an issue
  minorIssue: addIssue.bind(null, false), // helper to add an issue that shouldnt trigger a modal
  notifications: [],
  notice: addNotification,
  dismissNotice: removeNotification,

  // application state, fetched every refresh
  actionItems: {},
  indexCounts: {},
  channels: [], // array of { name: str, pinned: bool, lastUpdated: ts, hasNew: bool }
  user: {
    id: null,
    profile: {},
    needsSetup: false, // does the setup flow need to occur?
    followeds: [], // people the user follows
    friends: [], // people the user follows, who follow the user back
    nonfriendFolloweds: [], // people the user follows, who dont follow the user back
    nonfriendFollowers: [] // people the user doesnt follow, who follows the user
  },
  users: {
    names: {},
    profiles: {}
  },
  peers: [],
  isWifiMode: false
})
app.on('notice', app.notice)

function addIssue (isUrgent, title, err, extraIssueInfo) {
  console.error(title, err, extraIssueInfo)
  var message = err.message || err.toString()
  var stack   = err.stack || ''
  var issueDesc = message + '\n\n' + stack + '\n\n' + (extraIssueInfo||'')

  app.issues.unshift({
    isRead: false,
    isUrgent: isUrgent,
    title: title,
    message: message,
    stack: stack,
    issueUrl: 'https://github.com/ssbc/patchwork/issues/new?body='+encodeURIComponent(issueDesc)
  })
  app.emit('update:issues')
}

var notificationUuidCounter = 0
function addNotification (message) {
  app.notifications.push({ key: ++notificationUuidCounter, message: message, dismissAfter: 5e3 })
  app.emit('update:notifications')
}
function removeNotification (notification) {
  app.notifications = app.notifications.filter(n => n !== notification)
  app.emit('update:notifications')
}

function updateChannels (name, values) {
  // upsert
  var i
  for (i = 0; i < app.channels.length; i++) {
    if (app.channels[i].name === name) {
      // immutable update - create a new object, so shouldUpdate works nicely in the channels list com
      app.channels[i] = Object.assign({}, app.channels[i], values)
      break
    }
  }
  if (i === app.channels.length)
    app.channels.push(Object.assign({ name: name }, values))

  // sort
  sortChannels()
}

function sortChannels () {
  app.channels.sort(function (a, b) {
    // go by last updated
    return b.lastUpdated - a.lastUpdated
  })
}

function onPatchworkEvent (e) {
  if (e.type == 'index-change') {
    for (var k in e.counts)
      app.indexCounts[k] = e.counts[k]
    app.emit('update:indexCounts')
    updateTitle()
    if (e.index.indexOf('channel-') === 0) {
      updateChannels(e.index.slice('channel-'.length), { lastUpdated: Date.now() })
      app.emit('update:channels')      
    }
  }
  else if (e.type == 'isread') {
    app.emit('update:isread', { key: e.key, value: e.value })
  }
  else if (e.type == 'channelpinned') {
    updateChannels(e.channel, { pinned: e.value })
    app.emit('update:channels')
  }
  else if (e.type == 'channelwatched') {
    updateChannels(e.channel, { watched: e.value })
    app.emit('update:channels')
  }
}

function pollPeers () {
  app.ssb.gossip.peers(function (err, peers) {
    var isWifiMode = require('./util').getPubStats(peers).hasSyncIssue
    app.peers = peers
    app.emit('update:peers')
    if (isWifiMode !== app.isWifiMode) {
      app.isWifiMode = isWifiMode
      app.emit('update:isWifiMode')
    }
  })
}

function updateTitle () {
  document.title = t('Patchwork')
  favicon.update({ label: app.indexCounts.inboxUnread })
}
window.favicon = favicon

function fetchLatestState (cb) {
  if (!patchworkEventStream)
    pull((patchworkEventStream = app.ssb.patchwork.createEventStream()), pull.drain(onPatchworkEvent.bind(this)))
  if (!isWatchingNetwork) {
    setInterval(pollPeers, POLL_PEER_INTERVAL)
    isWatchingNetwork = true
  }

  var done = multicb({ pluck: 1 })
  app.ssb.whoami(done())
  app.ssb.patchwork.getNamesById(done())
  app.ssb.patchwork.getAllProfiles(done())
  app.ssb.patchwork.getActionItems(done())
  app.ssb.patchwork.getIndexCounts(done())
  app.ssb.gossip.peers(done())
  if (!app.channels.length)
    app.ssb.patchwork.getChannels(done())
  done(function (err, data) {
    if (err) throw err.message
    app.user.id         = data[0].id
    app.users.names     = data[1]
    app.users.profiles  = data[2]
    app.actionItems     = data[3]
    app.indexCounts     = data[4]
    app.peers           = data[5]
    if (data[6]) {
      app.channels        = data[6]
      sortChannels()
    }
    app.isWifiMode      = require('./util').getPubStats(app.peers).hasSyncIssue
    app.user.profile    = app.users.profiles[app.user.id]
    app.user.needsSetup = !app.users.names[app.user.id]
    updateTitle()

    // get friend list
    var social = require('patchkit-util/social')
    app.user.followeds = social.followeds(app.users, app.user.id)
    app.user.friends = app.user.followeds.filter(function (other) { return other !== app.user.id && social.follows(app.users, other, app.user.id) })
    app.user.nonfriendFolloweds = app.user.followeds.filter(function (other) { return other !== app.user.id && !social.follows(app.users, other, app.user.id) })
    app.user.nonfriendFollowers = social.unfollowedFollowers(app.users, app.user.id, app.user.id)

    // refresh suggest options for usernames
    app.suggestOptions['@'] = []
    if (app.user.profile) {
      for (var id in app.users.profiles) {
        if (id == app.user.profile.id || social.follows(app.users, app.user.id, id)) {
          var name = app.users.names[id]
          app.suggestOptions['@'].push({
            id: id,
            cls: 'user',        
            title: name || id,
            image: pu.getProfilePicUrl(app.users, id),
            subtitle: name || id,
            value: name ? ('@'+name) : id
          })
        }
      }
    }

    app.emit('update:all')
    cb && cb()
  })
}

var muxrpc     = require('muxrpc')
var pull       = require('pull-stream')
var ws         = require('pull-ws-server')
var Serializer = require('pull-serializer')
var t          = require('patchwork-translations')

module.exports = function () {
  // create rpc object
  var ssb = muxrpc(window.MANIFEST, false, serialize)()

  // setup rpc stream over websockets
  var protocol = (window.location.protocol == 'https:') ? 'wss:' : 'ws:'
  var stream = ws.connect(protocol+'//'+(window.location.hostname)+':7777', { onClose: onConnectionLost })
  pull(stream, ssb.createStream(), stream)
  return ssb
}

function serialize (stream) {
  return Serializer(stream, JSON, {split: '\n\n'})
}

function onConnectionLost () {
  var el = document.createElement('div')
  el.className = 'connection-lost'
  el.appendChild(document.createTextNode(t('connectionLost')))
  document.body.insertBefore(el, document.body.firstChild)
}

const ICON_PATH = '/img/icon.png'
const FONT = 'Helvetica, Arial, sans-serif'
var canvas = document.createElement('canvas')
var img = null
var label = false

module.exports.update = function (opts) {
  if ('label' in opts) {
    if (typeof opts.label == 'number' && opts.label > 0 && opts.label < 10)
      label = ' '+opts.label
    else
      label = opts.label
  }
  loadImg(draw)
}

function draw () {
  var head = document.getElementsByTagName('head')[0]
  var favicon = document.querySelector('link[rel=icon]')
  var newFavicon = document.createElement('link')
  var multiplier, fontSize, context, xOffset, yOffset, border, shadow

  // scale canvas elements based on favicon size
  multiplier = img.width / 16
  fontSize   = multiplier * 11
  xOffset    = multiplier
  yOffset    = multiplier * 11
  border     = multiplier
  shadow     = multiplier * 4

  canvas.height = canvas.width = img.width
  context = canvas.getContext('2d')
  context.font = 'bold ' + fontSize + 'px ' + FONT

  // draw favicon background
  if (label) { context.globalAlpha = 0.8 }
  context.drawImage(img, 0, 0)
  context.globalAlpha = 1.0

  if (label) {
    // draw drop shadow
    context.shadowColor = '#000'
    context.shadowBlur = shadow
    context.shadowOffsetX = 0
    context.shadowOffsetY = 0

    // draw border
    // context.fillStyle = '#000'
    // context.fillText(label, xOffset, yOffset)
    // context.fillText(label, xOffset + border, yOffset)
    // context.fillText(label, xOffset, yOffset + border)
    // context.fillText(label, xOffset + border, yOffset + border)

    // draw label
    context.fillStyle = '#FFF'
    context.fillText(label,
      xOffset + (border / 2.0),
      yOffset + (border / 2.0)
    )
  }

  // replace favicon with new favicon
  newFavicon.rel = 'icon'
  newFavicon.href = canvas.toDataURL('image/png')
  if (favicon) { head.removeChild(favicon) }
  head.appendChild(newFavicon)
}

var loadImgCbs
function loadImg (cb) {
  // already loaded?
  if (img)
    return cb() // go

  // queue active?
  if (loadImgCbs)
    return loadImgCbs.push(cb) // add to queue

  // start the queue
  loadImgCbs = [cb]

  // load image
  img = document.createElement('img')
  img.crossOrigin = 'anonymous'
  img.onload = () => {
    loadImgCbs.forEach(cb => cb())
    loadImgCbs = null
  }
  img.src = ICON_PATH
}

'use babel'
import React from 'react'
import ssbref from 'ssb-ref'
import app from './app'
import u from 'patchkit-util'
import social from 'patchkit-util/social'
import t from 'patchwork-translations'

const MAX_CHANNEL_RESULTS = 3
const MAX_USER_RESULTS = 3

export function getResults (query) {
  var results = []

  // ssb references
  if (ssbref.isLink(query)) {
    var shortened = u.shortString(query)
    if (ssbref.isFeedId(query))      results = [{ icon: 'user',     label: t('search.OpenUser', {id: shortened}),   fn: openObject }]
    else if (ssbref.isMsgId(query))  results = [{ icon: 'envelope', label: t('search.OpenMessage', {id: shortened}), fn: openObject }]
    else if (ssbref.isBlobId(query)) results = [{ icon: 'file',     label: t('search.OpenFile', {id: shortened}),    fn: openObject }]
    results.push({ icon: 'search', label: t('search.SearchForReferences', {id: shortened}), fn: doSearch({ type: 'mentions' }) })
    return results
  }

  // general results
  results = results.concat([
    { icon: 'envelope', label: t('search.SearchMessages', {query}), fn: doSearch({ type: 'posts' }) }
  ])

  // builtin pages
  // TODO

  // known users
  results = results.concat(getUserResults(query))

  // channels
  results = results.concat(getChannelResults(query))

  return results
}

function getUserResults (query) {
  if (query.charAt(0) == '#') // strip off the pound
    query = query.slice(1)
  query = query.toLowerCase()

  var results = []
  for (let id in app.users.names) {
    var name = app.users.names[id]
    if (typeof name == 'string' && name.toLowerCase().indexOf(query) !== -1)
      results.push(id)
  }
  // sort by popularity (isnt that just the way of things?)
  results.sort(social.sortByPopularity.bind(social, app.users))
  results = results
    .slice(0, MAX_USER_RESULTS)
    .map(id => { return { icon: 'user', label: t('search.OpenUser', {id: app.users.names[id]}), fn: () => openObject(id) } })
  return results
}

function getChannelResults (query) {
  if (query.charAt(0) == '#') // strip off the pound
    query = query.slice(1)
  query = query.toLowerCase()

  var hasExact = false
  var results = []
  for (var i=0; i < app.channels.length && results.length < MAX_CHANNEL_RESULTS; i++) {
    var ch = app.channels[i]
    if (ch.name.toLowerCase().indexOf(query) !== -1) {
      if (!hasExact)
        hasExact = (ch.name == query)
      results.push({
        icon: 'hashtag',
        label: <span>{t('search.OpenChannel', {name: ch.name})}</span>,
        fn: openChannel(ch.name)
      })
    }
  }
  if (!hasExact)
    results.push({ icon: 'hashtag', label: t('search.OpenChannel', {name: query}), fn: openChannel(query) })
  return results
}

function openObject (ref) {
  if (ssbref.isFeedId(ref)) {
    app.history.pushState(null, '/profile/'+encodeURIComponent(ref))
  } else if (ssbref.isMsgId(ref)) {
    app.history.pushState(null, '/msg/'+encodeURIComponent(ref))
  } else if (ssbref.isBlobId(ref)) {
    window.location = '/'+encodeURIComponent(ref)
  }
}

const openChannel = channel => () => {
  if (channel.charAt(0) == '#') // strip off the pound
    channel = channel.slice(1)
  app.history.pushState(null, '/channel/'+encodeURIComponent(channel))
}

export const doSearch = opts => query => {
  // TODO incorporate `opts`
  app.history.pushState(null, '/search/'+encodeURIComponent(query))
}
